mapper.select:
SELECT P.pdt_id, TG.tag_name, P.title, P.sub_title, P.ds_rate, P.price,P.image,P.sel_price,P.sales_rate, P.de_type,P.company,C.cat_code,CCTT.cd_type_name,CCT.cd_name,CCT.cat_code
FROM PRODUCT_TB P JOIN PRODUCT_TAG_TB T ON T.pdt_id = P.pdt_id JOIN TAG_TB TG ON TG.tag_id = T.tag_id JOIN CATEGORY_TB C ON C.pdt_id = P.pdt_id JOIN COMMON_CODE_TB CCT ON CCT.cat_code = C.cat_code JOIN COMMON_CODE_TYPE_TB CCTT ON CCTT.cd_type_id = CCT.cd_type_id WHERE CCTT.cd_type_id = 'P003' and CCT.cd_name= 'CCT.cd_name' WHERE TRUE
limit #{offset}, #{pageSize}

select DISTINCT CCT.cd_type_id, cd_name, cd_type_name, CCT.cd_name_num
from COMMON_CODE_TB CCT
         join COMMON_CODE_TYPE_TB CCTT on CCT.cd_type_id = CCTT.cd_type_id;
